mapper.select:
		select `id`,`createId`,`createTime`,`updateId`,`updateTime`,`state`,`parent_id`,`name`,`order_num`,`del_flag` from proposal_cbdw

		  		   and id = #{id} 
		  		   and createId = #{createid} 
		  		   and createTime = #{createtime} 
		  		   and updateId = #{updateid} 
		  		   and updateTime = #{updatetime} 
		  		   and state = #{state} 
		  		   and parent_id = #{parentId} 
		  		   and name like #{name} 
		  		   and order_num = #{orderNum} 
		  		   and del_flag = #{delFlag} 



                order by ${sort} ${order}


                order by order_num asc



			limit #{offset}, #{limit}

select count(*) from proposal_cbdw

  		   and id = #{id} 
  		   and createId = #{createid} 
  		   and createTime = #{createtime} 
  		   and updateId = #{updateid} 
  		   and updateTime = #{updatetime} 
  		   and state = #{state} 
  		   and parent_id = #{parentId} 
  		   and name like #{name} 
  		   and order_num = #{orderNum} 
  		   and del_flag = #{delFlag} 
	 	  and taxxid = #{taxxid}

		SELECT p1.*,p3.`name` parentName,s1.username userName FROM proposal_grwy p1 
		LEFT JOIN proposal_cbdw p2 ON p1.dwmcId=p2.id
		LEFT JOIN proposal_cbdw p3 ON p2.parent_id=p3.id
		LEFT JOIN sys_user s1 ON p1.userId=s1.user_id

		  		   and p1.id = #{id} 
		  		   and p1.createId = #{createid} 
		  		   and p1.createTime = #{createtime} 
		  		   and p1.updateId = #{updateid} 
		  		   and p1.updateTime = #{updatetime} 
		  		   and p1.state = #{state} 
		  		   and p1.ssjcId = #{ssjcid} 
		  		   and p1.ssjbId = #{ssjbid} 
		  		   and p1.ssdpId = #{ssdpid} 
		  		   and p1.sswyhId = #{sswyhid} 
		  		   and p1.ssdqId = #{ssdqid} 
		  		   and p1.xm = #{xm} 
		  		   and p1.xb = #{xb} 
		  		   and p1.sr_nian = #{srNian} 
		  		   and p1.sr_yue = #{srYue} 
		  		   and p1.jstx = #{jstx} 
		  		   and p1.dwmc_zw = #{dwmcZw} 
		  		   and p1.txdz = #{txdz} 
		  		   and p1.yb = #{yb} 
		  		   and p1.bglxdh = #{bglxdh} 
		  		   and p1.sjhm = #{sjhm} 
		  		   and p1.sjlx = #{sjlx} 
		  		   and p1.userId = #{userid} 
		  		   and p1.fzr = #{fzr} 
		  		   and p1.dwmc = #{dwmc} 
		  		   and p1.lxr = #{lxr} 
		  		   and p1.zw = #{zw} 
		  		   and p1.type = #{type} 
		  		   and p1.dwjc = #{dwjc} 
		  		   and (p2.id = #{cbdwId} or p3.id = #{cbdwId})
		  		   and p2.name like #{name}



                order by ${sort} ${order}


                order by p2.order_num asc



			limit #{offset}, #{limit}

SELECT count(*) FROM proposal_grwy p1 
LEFT JOIN proposal_cbdw p2 ON p1.dwmcId=p2.id
LEFT JOIN proposal_cbdw p3 ON p2.parent_id=p3.id
LEFT JOIN sys_user s1 ON p1.userId=s1.user_id

  		   and p1.id = #{id} 
  		   and p1.createId = #{createid} 
  		   and p1.createTime = #{createtime} 
  		   and p1.updateId = #{updateid} 
  		   and p1.updateTime = #{updatetime} 
  		   and p1.state = #{state} 
  		   and p1.ssjcId = #{ssjcid} 
  		   and p1.ssjbId = #{ssjbid} 
  		   and p1.ssdpId = #{ssdpid} 
  		   and p1.sswyhId = #{sswyhid} 
  		   and p1.ssdqId = #{ssdqid} 
  		   and p1.xm = #{xm} 
  		   and p1.xb = #{xb} 
  		   and p1.sr_nian = #{srNian} 
  		   and p1.sr_yue = #{srYue} 
  		   and p1.jstx = #{jstx} 
  		   and p1.dwmc_zw = #{dwmcZw} 
  		   and p1.txdz = #{txdz} 
  		   and p1.yb = #{yb} 
  		   and p1.bglxdh = #{bglxdh} 
  		   and p1.sjhm = #{sjhm} 
  		   and p1.sjlx = #{sjlx} 
  		   and p1.userId = #{userid} 
  		   and p1.fzr = #{fzr} 
  		   and p1.dwmc = #{dwmc} 
  		   and p1.lxr = #{lxr} 
  		   and p1.zw = #{zw} 
  		   and p1.type = #{type} 
