mapper.select:
		select * from proposal_xx

		  		   and id = #{id} 
		  		   and createId = #{createid} 
		  		   and createTime = #{createtime} 
		  		   and updateId = #{updateid} 
		  		   and updateTime = #{updatetime} 
		  		   and state = #{state} 
		  		   and type = #{type} 
		  		   and nr = #{nr} 
		  		   and fsr = #{fsr} 
		  		   and jsr = #{jsr} 
		  		   and cbdwId = #{cbdwid} 
		  		   and wyId = #{wyid} 
		  		   and taxxId = #{taxxid} 
		  		   and (fsr = #{br} or jsr = #{br}) 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from proposal_xx

  		   and id = #{id} 
  		   and createId = #{createid} 
  		   and createTime = #{createtime} 
  		   and updateId = #{updateid} 
  		   and updateTime = #{updatetime} 
  		   and state = #{state} 
  		   and type = #{type} 
  		   and nr = #{nr} 
  		   and fsr = #{fsr} 
  		   and jsr = #{jsr} 
  		   and cbdwId = #{cbdwid} 
  		   and wyId = #{wyid} 
  		   and taxxId = #{taxxid}

		select p1.*,p4.id taxxId,p4.tatm,p4.tah,p2.type fsrType,p2.xm fsrXm,p2.dwmc fsrDwmc,p3.type jsrType,p3.xm jsrXm,p3.dwmc jsrDwmc, 
		(CASE p2.type WHEN 1 THEN p2.xm WHEN 2 THEN p2.dwmc WHEN 3 THEN p2.dwmc ELSE p2.xm END) fsrMc,
		(CASE p3.type WHEN 1 THEN p3.xm WHEN 2 THEN p3.dwmc WHEN 3 THEN p3.dwmc ELSE p3.xm END) jsrMc
		from proposal_xx p1
		LEFT JOIN proposal_grwy p2 ON p1.fsr=p2.userId
		LEFT JOIN proposal_grwy p3 ON p1.jsr=p3.userId
		LEFT JOIN proposal_taxx p4 ON p1.taxxId=p4.id

		  		   and p1.id = #{id} 
		  		   and p1.createId = #{createid} 
		  		   and p1.createTime = #{createtime} 
		  		   and p1.updateId = #{updateid} 
		  		   and p1.updateTime = #{updatetime} 
		  		   and p1.state = #{state} 
		  		   and p1.type = #{type} 
		  		   and p1.nr = #{nr} 
		  		   and p1.fsr = #{fsr} 
		  		   and p1.jsr = #{jsr} 
		  		   and p1.cbdwId = #{cbdwid} 
		  		   and p1.wyId = #{wyid} 
		  		   and p1.taxxId = #{taxxid} 
		  		   and (p1.fsr = #{br} or p1.jsr = #{br}) 
		  		   and p4.tatm like #{keyword} 
		  		   and p4.id = #{proposalId} 



                order by ${sort} ${order}


                order by p1.id desc



			limit #{offset}, #{limit}

select count(p1.id) from proposal_xx p1
LEFT JOIN proposal_grwy p2 ON p1.fsr=p2.userId
LEFT JOIN proposal_grwy p3 ON p1.jsr=p3.userId
LEFT JOIN proposal_taxx p4 ON p1.taxxId=p4.id

  		   and p1.id = #{id} 
  		   and p1.createId = #{createid} 
  		   and p1.createTime = #{createtime} 
  		   and p1.updateId = #{updateid} 
  		   and p1.updateTime = #{updatetime} 
  		   and p1.state = #{state} 
  		   and p1.type = #{type} 
  		   and p1.nr = #{nr} 
  		   and p1.fsr = #{fsr} 
  		   and p1.jsr = #{jsr} 
  		   and p1.cbdwId = #{cbdwid} 
  		   and p1.wyId = #{wyid} 
  		   and p1.taxxId = #{taxxid} 
  		   and (p1.fsr = #{br} or p1.jsr = #{br})

select count(id) from proposal_xx	x1
left join sys_user s1 on x1.fsr=s1.user_id
where s1.name=#{name} and x1.taxxid=#{taxxid}
